import React, {Component} from 'react';
import Listavideos from './components/Listavideos';
import DetalhesVideo from './components/DetalhesVideo';
import Form from './components/Form';
import Api from './api/Api.js'
import {Route, Switch} from 'react-router-dom';
import './App.css';


class App extends Component {
  constructor(props){
    super(props);
    this.state = {
          videos:[] ,
          selectedVideos: null,
          totalResults:null,

    };
  }


  handleSubmit= async(termobusca) =>{
    const resposta= await Api.get('search', {
    params:{
      part:'snippet',
      maxResults:15,
      key:'AIzaSyCP8lufKgfTZ7tDE1gkRkaf4_DCyFXJ32o',
      q:termobusca
  }} );
    console.log(resposta)
    this.setState({videos: resposta.data.items, selectedVideos: resposta.data.items[0], totalResults: resposta.data.pageInfo.totalResults });
    console.log(this.state.videos)

  }
   onVideoSelect = (video )=>{
    console.log(video)
    this.setState({selectedVideos:video});
    console.log(this.state.selectedVideos)


  }  

  render(){
    return (
    <div >


           <Form  className="App" onFormSubmit={this.handleSubmit}/> 
           <Route exact path='/' render={(routeProps)=> <Listavideos {...routeProps} videos={this.state.videos} onVideoSelect={this.onVideoSelect} totalResults={this.state.totalResults} /> } /> 
           <Switch> 

            <Route exact path='/detalhes' render={(routeProps)=> <DetalhesVideo {...routeProps} video={this.state.selectedVideos}   /> } /> 
          </Switch>  






    </div>
  );

 }

}

export default App;
