import React, {Component} from 'react';
import Form from './components/Form';
import Listavideos from './components/Listavideos';
import DetalhesVideo from './components/DetalhesVideo';
import {Route, Switch} from 'react-router-dom';
import './App.css';
import Api from './api/Api.js'

class App extends Component {
  state={
    videos:[] ,
    selectedVideos:null,
  }
  onVideoSelect = (video )=>{
    this.setState({selectedVideos:this.state.video});
    console.log(this.state.video)
  }
  handleSubmit= async(termobusca) =>{
    const resposta= await Api.get('search', {
    params:{
      part:'snippet',
      maxResults:15,
      key:'AIzaSyCP8lufKgfTZ7tDE1gkRkaf4_DCyFXJ32o',
      q:termobusca
  }} );
    this.setState({videos: resposta.data.items, selectedVideos: resposta.data.items[0]});
    console.log(this.state.selectedVideos)
  }
  render(){
    return (
    <div >
        <Switch>
            <Route exact path='/' render={(routeProps)=> <Form className="App" {...routeProps} onFormSubmit={this.handleSubmit}/> } /> 
            <Route exact path='/selected' render={(routeProps)=> <DetalhesVideo  {...routeProps} video={this.selectedVideos}/> } />

        </Switch>
        <Listavideos videos={this.state.videos} onVideoSelect={this.onVideoSelect} />



    </div>
  );

 }

}

export default App;
