import React from 'react'
import {Link,Route, Switch} from 'react-router-dom';
import DetalhesVideo from './DetalhesVideo';

const Itens =({video, onVideoSelect}) => {
	
	return(

		<div className='videos'>
				
					<img style={{marginRight:'20px'}} alt='thumbnail' src = { video.snippet.thumbnails.medium.url} />
					<div style={{cursor:'pointer'}} onClick={ () => onVideoSelect(video)} >
						<Link to= '/detalhes/:videoId' >
							<h2>{video.snippet.title} </h2>
							<div>
								<p>{video.snippet.description} </p>
							</div> 
						</Link>	
						<Switch>
							<Route exact path='/detalhes/:videoId' render={(routeProps)=> <DetalhesVideo {...routeProps} videoId= {routeProps.match.params.name} video={video} /> } />
						</Switch>
					</div>
				
				
			
		</div>
		)
}

export default Itens;
