import React, {Component} from 'react';
import Page1 from './components/Page1';
import Page2 from './components/Page2';
import {Route, Switch} from 'react-router-dom';
import './App.css';
import Api from './api/Api.js'

class App extends Component {
  constructor(props){
    super(props);
    this.state={page:'page1' };
  }
  changePage(newPage){
    this.setState({page:newPage});
  }
  renderPage(){
    if (this.state.page ==='page1') return <Page1/>;
    else if(this.state.page ==='page2') return <Page2/>

  }
  render(){
    return (
    <div >
        <Switch>
            <Route exact path='/' render={(routeProps)=> <Page1 {...routeProps} /> } /> 
            <Route exact path='/detalhes' render={(routeProps)=> <Page2  {...routeProps}/> } />

        </Switch>




    </div>
  );

 }

}

export default App;
